import { sign, verify, JwtPayload } from 'jsonwebtoken';

import fs = require ('fs');

const SECRET = fs.readFileSync('jwt.evaluation.key', 'utf8');

class JwtMethods {
  private secret = SECRET;

  public generateToken = (payload: object) => sign(payload, this.secret, {
    algorithm: 'HS256',
    expiresIn: '7d',
  });

  public verifyToken = (token: string): JwtPayload =>
    verify(token, this.secret, { algorithms: ['HS256'] }) as JwtPayload;
}

export default JwtMethods;

import { NextFunction, Request, Response } from 'express';
import { IUserReq } from '../interfaces/login';
import StatusCode from '../utils/statusCode';

class VerifyValidFields {
  private statusCode = StatusCode;

  private regex = /^[\w-.]+@([\w-]+\.)+[\w-]{2,4}$/;

  verifyRequest = async (req: Request, res: Response, next: NextFunction) => {
    const userreq = req.body as IUserReq;
    if (!userreq.email || !userreq.password) {
      const message = 'All fields must be filled';
      return res.status(this.statusCode.Unauthorized).json({ message });
    }

    if (userreq.password.length < 7 || this.regex.test(userreq.email) === false) {
      const message = 'Incorrect email or password';
      return res.status(this.statusCode.Unauthorized).json({ message });
    }

    next();
  };
}

export default VerifyValidFields;

import { NextFunction, Request, Response } from 'express';
import IAuth from '../interfaces/express/IAuth';
import JwtMethods from '../utils/jwtMethods';
import StatusCode from '../utils/statusCode';

class ValidateAuth {
  private StatusCode = StatusCode;

  private jwtUtils = new JwtMethods();

  constructor() {
    this.verifyToken = this.verifyToken.bind(this);
  }

  async verifyToken(req: Request, res:Response, next: NextFunction) {
    const { authorization } = req.headers as unknown as IAuth;
    const message = 'usuario nao authenticado';
    if (!authorization || !authorization.length) {
      return res.status(this.StatusCode.Unauthorized)
        .json({ message });
    }

    try {
      const dataDecoded = this.jwtUtils.verifyToken(authorization);

      if (dataDecoded === null) {
        return res.status(this.StatusCode.Unauthorized).json({ message });
      }

      req.decodedUser = dataDecoded;

      next();
    } catch (err) {
      return res.status(this.StatusCode.Unauthorized).json({ message });
    }
  }
}

export default ValidateAuth;

